package week1.day2;

public class CheckPostiveNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=0;
		System.out.println("Enter the Numnber:"+n);
		if (n<0)
		{
			System.out.println("given number is Negative"+n);
		}
		else if(n>0) {
			System.out.println("given number is positive: "+n);
		}
		else {
			System.out.println("Given Number is neither positive nor negative");
		}
	}

}
