package week1.day2;

public class NegativeToPostive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=-40;
		if (n<0)
		{
			System.out.println("given number is "+n+" converted to: "+-n);
		}
		else {
			System.out.println("given number is positive: "+n);
		}
	}

}
